-- ----------------------------
--  Table structure for `wp_posts`
-- ----------------------------
DROP TABLE IF EXISTS `wp_posts`;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`(191)),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- ----------------------------
--  Records of `wp_posts`
-- ----------------------------
BEGIN;
INSERT INTO `wp_posts` VALUES ('1', '1', '2015-11-25 19:28:52', '2015-11-25 19:28:52', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2015-11-25 19:28:52', '2015-11-25 19:28:52', '', '0', 'http://localhost/hoverboard.nyc/?p=1', '0', 'post', '', '0'), ('2', '1', '2015-11-25 19:28:52', '2015-11-25 19:28:52', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/hoverboard.nyc/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2015-11-25 19:28:52', '2015-11-25 19:28:52', '', '0', 'http://localhost/hoverboard.nyc/?page_id=2', '0', 'page', '', '0'), ('3', '1', '2015-11-25 19:30:02', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2015-11-25 19:30:02', '0000-00-00 00:00:00', '', '0', 'http://localhost/hoverboard.nyc/?p=3', '0', 'post', '', '0'), ('4', '1', '2015-11-26 08:09:29', '2015-11-26 08:09:29', '', 'Shop', '', 'publish', 'closed', 'closed', '', 'shop', '', '', '2015-11-26 08:09:29', '2015-11-26 08:09:29', '', '0', 'http://localhost/hoverboard.nyc/index.php/shop/', '0', 'page', '', '0'), ('5', '1', '2015-11-26 08:09:30', '2015-11-26 08:09:30', '[woocommerce_cart]', 'Cart', '', 'publish', 'closed', 'closed', '', 'cart', '', '', '2015-11-26 08:09:30', '2015-11-26 08:09:30', '', '0', 'http://localhost/hoverboard.nyc/index.php/cart/', '0', 'page', '', '0'), ('6', '1', '2015-11-26 08:09:30', '2015-11-26 08:09:30', '[woocommerce_checkout]', 'Checkout', '', 'publish', 'closed', 'closed', '', 'checkout', '', '', '2015-11-26 08:09:30', '2015-11-26 08:09:30', '', '0', 'http://localhost/hoverboard.nyc/index.php/checkout/', '0', 'page', '', '0'), ('7', '1', '2015-11-26 08:09:30', '2015-11-26 08:09:30', '[woocommerce_my_account]', 'My Account', '', 'publish', 'closed', 'closed', '', 'my-account', '', '', '2015-11-26 08:09:30', '2015-11-26 08:09:30', '', '0', 'http://localhost/hoverboard.nyc/index.php/my-account/', '0', 'page', '', '0'), ('8', '1', '2015-11-26 08:15:57', '2015-11-26 08:15:57', '<meta charset=\"utf-8\"><meta charset=\"utf-8\">\r\n<p><strong>This product has 100% satisfaction, price and quality guarantee! Get your money back if matching quality is available at matching price or simply you are not satisfied.</strong></p>\r\n<p><script>// <![CDATA[\r\n(function(i,s,o,g,r,a,m){i[\'GoogleAnalyticsObject\']=r;i[r]=i[r]||function(){\r\n  (i[r].q=i[r].q||[]).push(arguments)},i[r].l=1*new Date();a=s.createElement(o),\r\n  m=s.getElementsByTagName(o)[0];a.async=1;a.src=g;m.parentNode.insertBefore(a,m)\r\n  })(window,document,\'script\',\'//www.google-analytics.com/analytics.js\',\'ga\');\r\n\r\n  ga(\'create\', \'UA-69967760-1\', \'auto\');\r\n  ga(\'send\', \'pageview\');\r\n// ]]></script><!-- --------------------------------------- --></p>\r\n<img src=\"https://cdn.shopify.com/s/files/1/1049/5096/files/satisfaction-guarantee.jpg?12882944290024241735\"><meta charset=\"utf-8\">\r\n<p>  <img src=\"https://cdn.shopify.com/s/files/1/1049/5096/files/promo1.png?12882944290024241735\"> <img src=\"https://cdn.shopify.com/s/files/1/1049/5096/files/promo2.png?12882944290024241735\"> <img src=\"https://cdn.shopify.com/s/files/1/1049/5096/files/promo3.png?12882944290024241735\"> <img src=\"https://cdn.shopify.com/s/files/1/1049/5096/files/promo4.png?12882944290024241735\"> <img src=\"https://cdn.shopify.com/s/files/1/1049/5096/files/promo5.png?12882944290024241735\"> <img src=\"https://cdn.shopify.com/s/files/1/1049/5096/files/promo6.png?12882944290024241735\"></p>\r\n<p>Fun goes hands-free with the Self Balancing Electric Scooter. This trend-forward transport will change the way you commute or play with its sensational self-balancing technology. Simply stand on the footpads and lean forward, using your feet to accelerate, slow down, or turn. Give it a go yourself or up your gift game with this amazing electric scooter. <br><br></p>\r\n<ul>\r\n<li>Includes hands-free electric scooter with rechargeable lithium battery, AC adapter, power cable, and user guide</li>\r\n<li>Reaches speeds of 2 to 10 mph</li>\r\n<li>Self-balancing technology</li>\r\n<li>LED lights on front</li>\r\n<li>Aluminum wheels with 6.5 inches premium quality rubber tires</li>\r\n<li>Charge time 2 to 3 hours</li>\r\n<li>Travels up to 20 miles on a full charge</li>\r\n<li>Minimum user height 4\'; minimum user weight 44 lbs; maximum user weight 220 lbs</li>\r\n<li>Shoes, helmet, and other appropriate safety gear should be worn at all times when riding</li>\r\n<li>Measures approximately 23\"L x 7-5/16\"W x 7-5/16\"H; weighs approximately 22.5 lbs</li>\r\n<li>UL listed; 1-year Limited Manufacturer\'s Warranty</li>\r\n<li>Imported</li>\r\n</ul>\r\n<meta charset=\"utf-8\">\r\n<p><span>It\'s like a scooter, but it is more easier to control direction than scooter</span><br><span>Easy to learn, you can learn how to use it in 2 minutes by consulting our videos gallery</span><br><span>Built in 4400mAh large capacity Samsung lithium-polymer battery, allows you travel approx 10-15 miles</span><br><span>Using mute motherboard, imported Samsung battery, shell surface paint handling</span><br><span>Slightly tilt your feet to control the direction, quite convenient to operate</span><br><span>Turn around freely, cool looking</span><br><span>Great vehicle for outdoor, suitable for short distance office worker</span><br><span>Nice Gadget to show yourself</span></p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<th>\r\n<p>General</p>\r\n</th>\r\n<td>Model Number: Q3 <br> Type: Self Balancing Scooter <br> For: Office Workers, Teenagers <br> Color: Blue, Black, White <br> Charger Type: US Plug, EU Plug</td>\r\n</tr>\r\n<tr>\r\n<th>\r\n<p>Parameters</p>\r\n</th>\r\n<td>Max Payload: 120kg <br> Maximum Speed (km/h): 20km/h <br> Mileage (depends on road and driver weight): 15-20km <br> Speed Limit Warning: Up to 15km/h <br> Permissible Gradient (depends on your weight): 15-30 degree <br> Battery: 4400mAh lithium battery <br> Charging Time: 120 Minutes <br> Charging Voltage: AC 110-220V 50/60Hz <br> Low Battery Warning: When battery is lower than 10 percent there will be warning sound to remind you <br> Tire Diameter: 6.68 inches <br> Pedal Ground Clearance (no weight bearing): 11.2cm <br> Working Temperature: -10 to +40 celsius degree</td>\r\n</tr>\r\n<tr>\r\n<th>\r\n<p>Basic Information</p>\r\n</th>\r\n<td>Product Weight: 12.000 kg <br> Package Weight: 13.100 kg <br> Product Dimension: 58.4 x 16.8 x 17.8 cm / 22.95 x 6.60 x 7.00 inches <br> Package Dimension: 65.0 x 25.0 x 24.0 cm / 25.55 x 9.83 x 9.43 inches</td>\r\n</tr>\r\n<tr>\r\n<th>\r\n<p>Package</p>\r\n</th>\r\n<td>Package Contents: 1 x Electric Scooter, 1 x EU Plug Charger, 1 x English User Manual</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Please consult the manufacturer\'s documentation regarding the safe and proper use, handling, storage, charging, and disposal of products containing lithium-ion batteries.</p>\r\n<p> </p>\r\n<!-- Facebook Pixel Code --><script>// <![CDATA[\r\n!function(f,b,e,v,n,t,s){if(f.fbq)return;n=f.fbq=function(){n.callMethod?\r\nn.callMethod.apply(n,arguments):n.queue.push(arguments)};if(!f._fbq)f._fbq=n;\r\nn.push=n;n.loaded=!0;n.version=\'2.0\';n.queue=[];t=b.createElement(e);t.async=!0;\r\nt.src=v;s=b.getElementsByTagName(e)[0];s.parentNode.insertBefore(t,s)}(window,\r\ndocument,\'script\',\'//connect.facebook.net/en_US/fbevents.js\');\r\n\r\nfbq(\'init\', \'780958502032479\');\r\nfbq(\'track\', \"PageView\");\r\n// ]]></script>\r\n<noscript><img height=\"1\" width=\"1\" style=\"display:none\" src=\"https://www.facebook.com/tr?id=780958502032479&amp;ev=PageView&amp;noscript=1\"></noscript>\r\n<!-- End Facebook Pixel Code -->\r\n\r\n\r\n<a href=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/5.jpg\"><img class=\"alignnone size-medium wp-image-9\" src=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/5-300x214.jpg\" alt=\"5\" width=\"300\" height=\"214\" /></a> <a href=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/4.jpg\"><img class=\"alignnone size-medium wp-image-10\" src=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/4-300x214.jpg\" alt=\"4\" width=\"300\" height=\"214\" /></a> <a href=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/3.jpg\"><img class=\"alignnone size-medium wp-image-11\" src=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/3-300x214.jpg\" alt=\"3\" width=\"300\" height=\"214\" /></a> <a href=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/2.jpg\"><img class=\"alignnone size-medium wp-image-12\" src=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/2-300x214.jpg\" alt=\"2\" width=\"300\" height=\"214\" /></a> <a href=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/1.jpg\"><img class=\"alignnone size-medium wp-image-13\" src=\"http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/1-300x214.jpg\" alt=\"1\" width=\"300\" height=\"214\" /></a>', 'Regular 6.5 inches Two Wheel Self Balancing Smart Electric Scooter with 4400 mAh Samsung Battery and LED Lights', '', 'publish', 'open', 'closed', '', 'regular-6-5-inches-two-wheel-self-balancing-smart-electric-scooter-with-4400-mah-samsung-battery-and-led-lights', '', '', '2015-11-26 08:15:57', '2015-11-26 08:15:57', '', '0', 'http://localhost/hoverboard.nyc/?post_type=product&#038;p=8', '0', 'product', '', '0'), ('9', '1', '2015-11-26 08:14:23', '2015-11-26 08:14:23', '', '5', '', 'inherit', 'open', 'closed', '', '5', '', '', '2015-11-26 08:14:23', '2015-11-26 08:14:23', '', '8', 'http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/5.jpg', '0', 'attachment', 'image/jpeg', '0'), ('10', '1', '2015-11-26 08:14:23', '2015-11-26 08:14:23', '', '4', '', 'inherit', 'open', 'closed', '', '4', '', '', '2015-11-26 08:14:23', '2015-11-26 08:14:23', '', '8', 'http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/4.jpg', '0', 'attachment', 'image/jpeg', '0'), ('11', '1', '2015-11-26 08:14:24', '2015-11-26 08:14:24', '', '3', '', 'inherit', 'open', 'closed', '', '3', '', '', '2015-11-26 08:14:24', '2015-11-26 08:14:24', '', '8', 'http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/3.jpg', '0', 'attachment', 'image/jpeg', '0'), ('12', '1', '2015-11-26 08:14:25', '2015-11-26 08:14:25', '', '2', '', 'inherit', 'open', 'closed', '', '2', '', '', '2015-11-26 08:14:25', '2015-11-26 08:14:25', '', '8', 'http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/2.jpg', '0', 'attachment', 'image/jpeg', '0'), ('13', '1', '2015-11-26 08:14:25', '2015-11-26 08:14:25', '', '1', '', 'inherit', 'open', 'closed', '', '1', '', '', '2015-11-26 08:14:25', '2015-11-26 08:14:25', '', '8', 'http://localhost/hoverboard.nyc/wp-content/uploads/2015/11/1.jpg', '0', 'attachment', 'image/jpeg', '0');
COMMIT;

